@book{bhatnagarSystematicReviewSports2019,
  title = {A Systematic Review of Sports Analytics},
  author = {Bhatnagar, Rakshit and Babbar, Mridul},
  date = {2019-03-29},
  abstract = {The interest from both academics and practitioners in the application of big data analytics (BDA) in sports has been rapidly growing which has resulted in the increasing need to research, develop and explore new techniques. This review proposed a framework that provides a full picture of current literature on where and how Analytics has been applied within the sports industry. We will try to present an overview of major analytical tools and technologies creating value in the sport industry, with the major focus on soccer, basketball and cricket. Unlike other sports, cricket is yet to see more researches from a perspective of sports analytics. The researches reviewed in this paper employs various techniques to measure and improve player‚Äôs performance such as developing a player‚Äôs ranking model, visual analysis to identify movement patterns and study numerical metrics during important events of the game. The present paper discusses how big data and modern Business Intelligence tools \& technologies may help to address the big data issues and aid in developing a theoretical model for tactical decision making in team sports. Use of Business Intelligence and Analytics (BI\&A) in competitive sports is still emerging, we identify a set of avenues for future research that will stimulate further development in sports analytics.}
}

